import React from 'react'
import {useEffect, useState} from "react";

const FoodList = () => {
    const [foodList, setFoodList] = useState([])

    useEffect(() => {
        getFoodList();
         console.log("what", foodList);
    }, []);

    const getFoodList = async () => {
        const check = localStorage.getItem("foodList");

        if (check) {
            setFoodList(JSON.parse(check));
        } else {
            const data = await fetch (`https://gogos-recipes-backend.herokuapp.com/api/recipes`);
           // const data = await api.json();
            localStorage.setItem("foodList", JSON.stringify(data.data));
            setFoodList(data.data);

        }
    }
  return (
    <div>
        <h1>Recent recipes</h1>

        <div>
            {foodList.map((item, index ) => {
                <div key={index}>
                    <img src={item.image} alt={item.name} />
                    <div>
                        <p>{item.name}</p>
                    </div>
                </div>
            })}
        </div>


    </div>
  )
}

export default FoodList
