import React from 'react';
import Navbar from '../components/Navbar';
import {useState, useEffect} from 'react'

function Recipe() {
    const [recipe, setRecipe] = useState({})



    const getRecipe = async () => {
        const api = await fetch (`https://gogos-recipes-backend.herokuapp.com/api/recipes/631ab8611fdf962172d3c778`)
        const data = await api.json();
        setRecipe(data.data)
    }
    useEffect( () => {
        getRecipe();
        console.log("recipe =>", recipe);
    }, [] );
  return (
    <div  >
        <Navbar />

        <div className='max-w-[1640px] mx-auto p-4 py-12 flex h-screen'>

            <h1 className='mb-2 font-bold text-gray-800 text-2xl md:text-3xl'>{recipe.name}</h1>
            <div className='block'>
                <img src={recipe.image} alt={recipe.name} />
            </div>

           <div className='block'>
            <h2 className='mb-2 font-bold text-gray-800 text-2xl md:text-3xl'>Description</h2>
             <p>{recipe.description}</p>
           </div>

        </div>


    </div>
  )
}

export default Recipe
