import React from 'react';
import Navbar from '../components/Navbar';
import {useState, useEffect} from 'react'

function Recipe() {
    const [recipe, setRecipe] = useState({})

    useEffect( () => {
        getRecipe();
        console.log("recipe =>", recipe);
    }, [] );

    const getRecipe = async () => {
        const api = await fetch (`https://gogos-recipes-backend.herokuapp.com/api/recipes/631ab8611fdf962172d3c778`)
        const data = await api.json();
        console.log("data", data.data)
        setRecipe(data.data)
    }

  return (
    <div>
        <Navbar />
<div>
      {/* ðŸ‘‡ï¸ iterate object KEYS */}
      {Object.keys(recipe).map((key, index) => {
        return (
          <div key={index}>
            <h2>
              {key}: {recipe[key]}
            </h2>

            <hr />
          </div>
        );
      })}

      <br />
      <br />
      <br />

      {/* ðŸ‘‡ï¸ iterate object VALUES */}
      {Object.values(recipe).map((value, index) => {
        return (
          <div key={index}>
            <h2>{value}</h2>

            <hr />
          </div>
        );
      })}
    </div>
    </div>
  )
}

export default Recipe
