import React from 'react';
import Navbar from '../components/Navbar';
import {useState, useEffect} from 'react'
import {useParams} from 'react-router-dom'

function Recipe() {
    let params = useParams();
    const [recipe, setRecipe] = useState({})



    const getRecipe = async () => {
        const api = await fetch (`https://gogos-recipes-backend.herokuapp.com/api/recipes/${params.id}`)
        const data = await api.json();
        setRecipe(data.data)
    }
    useEffect( () => {
        getRecipe();
    }, [] );
  return (
    <div  >
        <Navbar />

        <div className='max-w-[1640px] mx-auto p-4 py-12  '>

            <h1 className='mb-2 font-bold text-gray-800 text-2xl md:text-3xl'>{recipe.name}</h1>

            <img src={recipe.image} alt={recipe.name} />

            <h2 className='mb-2 font-bold text-gray-800 text-2xl md:text-3xl'>Description</h2>
            <p>{recipe.description}</p>


        </div>

        <section class="h-[640px] bg-white"><div class="max-w-7xl px-5 py-20 flex space-x-5 w-full h-full items-center justify-center mx-auto"><div class="flex flex-col space-y-5 w-full h-full"><div class="flex h-1/2 w-full bg-gray-300 rounded-md"></div><div class="flex h-1/2 w-full bg-gray-300 rounded-md"></div></div><div class="flex h-full w-full bg-gray-300 rounded-md"></div></div></section>


    </div>
  )
}

export default Recipe
